import React, { useContext, useState } from 'react'
import {Form, Button, Navbar, Offcanvas, Nav, NavDropdown} from 'react-bootstrap'
import {useNavigate} from 'react-router-dom'
import {LinkContainer} from 'react-router-bootstrap'

import ProductsContext from './context/ProductsContext'

const Search = () => {
  const { setProducts, currentCustomer, setCurrentCustomer} = useContext(ProductsContext)
  const navigate = useNavigate()

  const [formData, setFormData] = useState(
      {
        search_term: " "
      }
    )
  async function searchItem(event){
    event.preventDefault();
    if(formData.search_term === " "){
      alert("Enter a search term")
    }else{
      navigate('/loading')
      let response = await fetch("/search", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(formData)
      })
      if(response.ok){
      let  data = await response.json();
      console.log(data)
      setProducts([...data])
      }
      formData.search_term = " "
      }

  }

  function handleChange(event){
    setFormData({...formData, [event.target.name]: event.target.value})
  }

  function logOut(){
    setCurrentCustomer(null);
  }


      return (
        <div>
        <Navbar expand="lg" fixed='top'>
          <Navbar.Brand className='app-brand'>
          {currentCustomer ? (<h1>Customer</h1>) : (<><img src='../images/Shopcrawl.png' alt='Shopcrawl'/><h1>Shopcrawl</h1></>)}
          </Navbar.Brand>
          <Navbar.Toggle></Navbar.Toggle>
          <Navbar.Offcanvas>
          <Offcanvas.Header closeButton>
                    <Offcanvas.Title >
                      Shopcrawl
                    </Offcanvas.Title>
          </Offcanvas.Header>
            <Offcanvas.Body>
            <Nav className="justify-content-center flex-grow-1">
                <LinkContainer to='/'>
                  <Nav.Link>Home</Nav.Link>
                </LinkContainer>
                <LinkContainer to='/link'>
                  <Nav.Link>Link</Nav.Link>
                </LinkContainer>
                  <Form onSubmit={searchItem} className='d-flex'>
                  <Form.Control
                            type="search"
                            placeholder="Search"
                            value={formData.search_term}
                            onChange={handleChange}
                            name="search_term"
                            className="me-2"
                            aria-label="Search"
                          />
                  <Button variant="warning" onClick={searchItem}>Search</Button>
                </Form>
              </Nav>



               {currentCustomer ? (
                <Button variant='success' onClick={logOut}>Logout</Button>
               ) : (<Nav className='me-auto'>
                <Button className='account-btn' variant='warning'>
                    <i class="bi bi-person-fill"></i>
                      <NavDropdown
                      title="Account"
                      id="navbarScrollingDropdown"
                      >
                      <NavDropdown.Item>
                        <LinkContainer to='/sign-in'>
                          <Nav.Link>
                            <Button variant='warning'>Sign In</Button>
                          </Nav.Link>
                        </LinkContainer>
                      </NavDropdown.Item>
                      <NavDropdown.Item>
                        <LinkContainer to='/register'>
                          <Nav.Link>
                            <Button variant='warning'>Register</Button>
                          </Nav.Link>
                        </LinkContainer>
                      </NavDropdown.Item>
                      </NavDropdown>
                </Button>
                </Nav>
               )}


            </Offcanvas.Body>

          </Navbar.Offcanvas>

        </Navbar>
        </div>
      )


}

export default Search
