import React, {useContext, useEffect, useState} from 'react'
import ProductsContext from '../../context/ProductsContext'
import SingleHistory from './SingleHistory'
import { Container, Table } from 'react-bootstrap'

const SearchHistories = () => {
  const {currentCustomer} = useContext(ProductsContext)

  const [histories, setHistories] = useState(currentCustomer.search_histories)

  async function fetchHistories(){
    let response = await fetch("/my-history")
    if(response.ok){
      let fetchedHistories = await response.json();
      setHistories([...fetchedHistories])
    }
  }

  useEffect(() => {
    fetchHistories()
  }, [])
  if(currentCustomer){
     return (
    <div>
      <Container>
        <h4>My search history</h4>
        <Table>
          <thead>
            <th></th>
            <th><h5>Title</h5></th>
          </thead>
          <tbody>
             {histories.map(history => <SingleHistory history= {history}/>)}
          </tbody>
        </Table>
      </Container>

    </div>
  )
  }

}

export default SearchHistories
