import React, { useContext, useState } from 'react'
import {Form, Button, Navbar, Offcanvas, Nav, Dropdown} from 'react-bootstrap'
import {useNavigate} from 'react-router-dom'
import {LinkContainer} from 'react-router-bootstrap'

import Logo from '../images/logo.png'


import ProductsContext from './context/ProductsContext'

const Search = () => {
  const { setProducts, currentCustomer, setCurrentCustomer} = useContext(ProductsContext)
  const navigate = useNavigate()

  const [formData, setFormData] = useState(
      {
        search_term: " "
      }
    )
  async function searchItem(event){
    event.preventDefault();
    if(formData.search_term === " "){
      alert("Enter a search term")
    }else{
      navigate('/loading')
      let response = await fetch("/search", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(formData)
      })
      if(response.ok){
      let  data = await response.json();
      console.log(data)
      setProducts([...data])
      }
      formData.search_term = " "
      }

  }

  function handleChange(event){
    setFormData({...formData, [event.target.name]: event.target.value})
  }

  function logOut(){
    setCurrentCustomer(null);
  }


      return (
        <div>
        <Navbar expand="lg" fixed='top'>
          {currentCustomer ? (
            <Navbar.Brand className='app-brand'>
              <h1>Customer</h1>
            </Navbar.Brand>
          ) : (
            <Navbar.Brand className='app-brand'>
            <Nav>
              <img src={Logo} alt='Logo' className='brand-img'/>
              <h3 className='logo-title'>Shopcrawl</h3>
            </Nav>

            </Navbar.Brand>
          )}

          <Navbar.Toggle></Navbar.Toggle>
          <Navbar.Offcanvas className='me-auto'>
          <Offcanvas.Header closeButton>
                    <Offcanvas.Title >
                      Shopcrawl
                    </Offcanvas.Title>
          </Offcanvas.Header>
            <Offcanvas.Body>
            <Nav className="justify-content-center flex-grow-1">
                {/* <LinkContainer to='/'>
                  <Nav.Link>Home</Nav.Link>
                </LinkContainer>
                <LinkContainer to='/link'>
                  <Nav.Link>Link</Nav.Link>
                </LinkContainer> */}
                  <Form onSubmit={searchItem} className='d-flex' style={{width: "50%"}}>
                  <Form.Control
                            type="search"
                            placeholder="Search"
                            value={formData.search_term}
                            onChange={handleChange}
                            name="search_term"
                            className="me-2"
                            aria-label="Search"
                          />
                  <Button variant="warning" onClick={searchItem}>Search</Button>
                </Form>
              </Nav>



               {currentCustomer ? (
                <Button variant='success' onClick={logOut}>Logout</Button>
               ) : (<Nav className='me-auto'>
                {/* <Button className='account-btn' variant='warning'> */}
                    {/* <i class="bi bi-person-fill"></i> */}
                    <Dropdown >
                    <Dropdown.Toggle className='account' variant='dark'><i class="bi bi-person" ></i>Account</Dropdown.Toggle>
                      <Dropdown.Menu>
                        <Dropdown.Item>
                        <LinkContainer to='/sign-in'>
                          <Nav.Link>
                            Sign In
                          </Nav.Link>
                        </LinkContainer>
                      </Dropdown.Item>
                      <Dropdown.Item>
                        <LinkContainer to='/register'>
                          <Nav.Link>
                            Register
                          </Nav.Link>
                        </LinkContainer>
                      </Dropdown.Item>
                      </Dropdown.Menu>

                      </Dropdown>
                {/* </Button> */}
                </Nav>
               )}


            </Offcanvas.Body>

          </Navbar.Offcanvas>

        </Navbar>
        <Nav
      activeKey="/home"
      onSelect={(selectedKey) => alert(`selected ${selectedKey}`)}
      style={{marginTop: "100px"}}
    >
      <Nav.Item>
        <Nav.Link href="/home">Active</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link eventKey="link-1">Link</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link eventKey="link-2">Link</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link eventKey="disabled" disabled>
          Disabled
        </Nav.Link>
      </Nav.Item>
    </Nav>
        </div>
      )


}

export default Search
