import React, { useContext, useState } from 'react'
import { Card } from 'react-bootstrap'
import {useNavigate, Link } from 'react-router-dom'
import ProductsContext from '../../context/ProductsContext'

const SingleProduct = ({fProduct}) => {
  const navigate = useNavigate()
  const {setProducts} = useContext(ProductsContext)

  const [formData, setFormData] = useState(
    {
      search_term: " "
    }
  )
async function searchItem(){
  formData.search_term = fProduct.name
  if(formData.search_term === " "){
    alert("Enter a search term")
  }else{
      navigate('/loading')
      let response = await fetch("/search", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(formData)
      })
      if(response.ok){
      let  data = await response.json();
      console.log(data)
      setProducts([...data])
      }else{
        navigate("/error")
      }
      formData.search_term = " "
      setFormData({...formData})
    }

}


  return (
    <Link onClick={searchItem}>
      <Card className='home-product' >
          <Card.Img src={fProduct.image} ></Card.Img>
          <Card.Body>
            <Card.Title>{fProduct.name}</Card.Title>
          </Card.Body>
      </Card>
    </Link>

  )
}

export default SingleProduct
